#!/bin/bash
# 🔍 Script di Diagnostica Bot sulla VPS
# Esegue gli script di diagnostica per capire perché il bot non apre posizioni

echo "🔍 DIAGNOSTICA BOT SULLA VPS"
echo "============================"
echo ""

# Simboli da analizzare (passati come argomenti o default)
SYMBOLS=${1:-"ethereum avax_usdt sand uniswap"}

echo "Simboli da analizzare: ${SYMBOLS}"
echo ""

# Comando SSH (modifica se necessario)
VPS_HOST="ticket.logikaservice.it"
VPS_USER="root"
VPS_DIR="/var/www/ticketapp/backend"

echo "Connettendo a ${VPS_USER}@${VPS_HOST}..."
echo ""

